js:
Highcharts.getJSON('https://demo-live-data.highcharts.com/aapl-c.json', function (data) {

    // Create the chart
    Highcharts.stockChart('container', {


        rangeSelector: {
            selected: 1
        },

        title: {
            text: 'AAPL Stock Price'
        },

        series: [{
            name: 'AAPL Stock Price',
            data: data,
            marker: {
                enabled: true,
                radius: 3
            },
            shadow: true,
            tooltip: {
                valueDecimals: 2
            }
        }]
    });
});

css:
#container {
    height: 400px;
    min-width: 500px;
}

.info {
    font: 0.7em sans-serif;
    padding: 0.5em;
    margin: 0.5em;
    background: #fcffc5;
    border-radius: 0.5em;
    border: 1px solid gray;
}

.info::before {
    content: "i";
    margin: 0.5em;
    font: 1.5em serif bold;
    font-style: italic;
    color: green;
}

