js:
Highcharts.stockChart('container', {

    chart: {
        width: 800
    },

    xAxis: {
        dateTimeLabelFormats: {
            week: '%a,<br/>%e. %b'
        },
        startOfWeek: 0,
        tickPixelInterval: 70
    },

    rangeSelector: {
        selected: 1
    },

    series: [{
        name: 'USD to EUR',
        data: usdeur
    }]
});

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Exporting from 'highcharts/modules/exporting';
import Usdeur    from 'https://www.highcharts.com/samples/data/usdeur.js';
import Zh_cn     from 'https://code.hcharts.cn/plugins/zh_cn.js';

// 初始化模块
Exporting(Highcharts);
Usdeur(Highcharts);
